INSERT INTO `reviews` (`id`, `books_id`, `reader_id`, `rating`, `comment`, `date`) VALUES (156,36,28,5,'А хорошо написано, не без переборов(ну кто в здравой памяти будет рассказывать, что врага отпустил?), но очень хорошо! Ещё одной хорошей книгой стало больше!','2017-10-04');
