INSERT INTO `reviews` (`id`, `books_id`, `reader_id`, `rating`, `comment`, `date`) VALUES (170,40,34,5,'Офигенная книга. Если будет продолжение вообще описаюсь от восторга!','2016-06-30');
INSERT INTO `reviews` (`id`, `books_id`, `reader_id`, `rating`, `comment`, `date`) VALUES (171,40,15,5,'Книга то хорошая, интереснаятолько вот скорей всего продолжение ещё долгонько ждать придётся...','2017-01-10');
INSERT INTO `reviews` (`id`, `books_id`, `reader_id`, `rating`, `comment`, `date`) VALUES (172,40,17,5,'Вещь скорее продолжение вышло но тёмный паладин автора все-таки круче написан','2017-02-07');
INSERT INTO `reviews` (`id`, `books_id`, `reader_id`, `rating`, `comment`, `date`) VALUES (173,40,18,5,'отлично. Просто отлично. Смешение РПГ и EVE. А то уже приелись эльфы и гномы. Идея новая и надеюсь и продолжатели найдутся. По окончанию видно, что продолжение будет. Прочитаю','2017-03-16');
