INSERT INTO `reviews` (`id`, `books_id`, `reader_id`, `rating`, `comment`, `date`) VALUES (177,40,33,5,'Перечитал. Опять увлекло. Очень понравилось. Продолжения уже очень давно нет, видимо, и совсем не будет. Основные линии-завязки романа развязаны, непонятно, о чем продолжению быть. Спасибо автору и сайту, вещь интересная.','2019-10-12');
