INSERT INTO `reviews` (`id`, `books_id`, `reader_id`, `rating`, `comment`, `date`) VALUES (181,41,28,5,'Сыну (6 лет) очень понравилась эта книга!Весёлая и интересная, познавательная книга. Не успев закончить читать, сын просит продолжение. Спасибо великому автору за истории про Незнайку!','2020-05-20');
INSERT INTO `reviews` (`id`, `books_id`, `reader_id`, `rating`, `comment`, `date`) VALUES (182,41,19,5,'Очень круто. Аж захватывает дух .Минусов нет. Правда в одной главе начало было скучным, а так все интересно. Но по одной главе о всей книги можно не судить. Не все моменты в книги являются важными. И конец лично мне не был понятным. ','2020-02-03');
INSERT INTO `reviews` (`id`, `books_id`, `reader_id`, `rating`, `comment`, `date`) VALUES (183,41,14,4,'Книга Н. Носова «Незнайка в солнечном городе» мне очень понравились. В ней по сравнению с первой частью появились новые герои – друзья и злодеи, сюжет стал более динамичным и насыщенным событиями. Жаль только, что первая и вторая части не связаны между собой.','2020-09-16');
