INSERT INTO `reviews` (`id`, `books_id`, `reader_id`, `rating`, `comment`, `date`) VALUES (204,47,10,5,'Интересная книга.Не ожидал,но втянулся и прочитал.Гг распутал старое преступление и не очень рад,поскольку опасается что может попасть под сокращение.Преступнику больше не совершить ни одного преступления.Он обездвижен.','2021-10-13');
