INSERT INTO `reviews` (`id`, `books_id`, `reader_id`, `rating`, `comment`, `date`) VALUES (222,49,19,5,'Очень понравилось! Конечно, некоторые моменты не совсем понятны чисто с медицинской точки зрения, но в остальном есть о чём подумать. Хотя и сразу догадалась кто за всем этим стоит!','2020-05-06');
INSERT INTO `reviews` (`id`, `books_id`, `reader_id`, `rating`, `comment`, `date`) VALUES (223,49,23,5,'Очень и очень неплохо... Собственно..это давно известный факт..что миром правят психопаты..но и тем не менее интересно почитать..чисто с медицинской психиатрической точки зрения..','2020-05-26');
INSERT INTO `reviews` (`id`, `books_id`, `reader_id`, `rating`, `comment`, `date`) VALUES (224,49,36,5,'Очень рекомендую! Сюжет необычный и хотя догадка кто главный злодей не самая трудная, книга чрезвычайно интересная.','2020-11-18');
INSERT INTO `reviews` (`id`, `books_id`, `reader_id`, `rating`, `comment`, `date`) VALUES (225,49,32,5,'Напряженненький сюжетец. Иногда сложно сразу вспомнить кто есть кто - многовато временных скачков в повествовании.','2021-04-16');
INSERT INTO `reviews` (`id`, `books_id`, `reader_id`, `rating`, `comment`, `date`) VALUES (226,50,26,5,'В книге затронута очень тяжелая тема: с одной стороны - это ужасы сирийской войны, с другой - война с личными демонами, которые вторглись в жизнь всех членов семьи главной героини, от мала до велика. Эти демоны порождают такие страхи, кошмары, что не представляешь, как с ними бороться (про победу над ними речи даже не идет).  ','2021-01-23');
INSERT INTO `reviews` (`id`, `books_id`, `reader_id`, `rating`, `comment`, `date`) VALUES (227,51,28,3,'Занудно. Читать не тянет. Диагональный метод чтения не дал никакого результата. Кажется, что в этой эпопее всё заранее известно с самого начала. Может быть, просто не моё.','2021-03-12');
INSERT INTO `reviews` (`id`, `books_id`, `reader_id`, `rating`, `comment`, `date`) VALUES (228,56,18,5,'Я, наверное, привыкла к стилю автора и динамике развития сюжета, потому что эта книга мне нравится и не кажется занудной. Тут нет драйва и острого напряжения, но много чувств и эмоций. Как всегда, удачные персонажи, интересное сплетение судеб. Перечитывала несколько раз и, думаю, перечитаю еще.','2017-02-15');
INSERT INTO `reviews` (`id`, `books_id`, `reader_id`, `rating`, `comment`, `date`) VALUES (229,56,21,4,'Маленько занудно, но тем не менее интересно. Автор верен сам себе.','2018-10-24');
INSERT INTO `reviews` (`id`, `books_id`, `reader_id`, `rating`, `comment`, `date`) VALUES (230,56,34,4,'Книга интересная. Не то что бы она стала \"любимой-читаемой до дыр\", но все же... Сюжет не плохой, интересные герои прошлых книг присутствуют даже, это мне больше всего нравится в книгах Устиновой. Свой мир, нет чужих: свой майор следователь, свой серьезный человек олигарх.... ','2019-02-14');
